import React from 'react';
import Christian from '../../images/team/Christian.jpg';
import Shane from '../../images/team/Shane.jpg'

const Team = ({ height, width }) => {
  const titleStyle = {
    borderBottom: "2px solid #fff",
    maxWidth: "500px",
    margin: "auto",
    padding: "10px"
  }

  const handleImage = (name) => {
    return (
      <div className="col-4">
        <div className="m-4">
          <img src={name === 'shane' ? Shane : Christian} className="rounded mw-100" alt="Shane" />
        </div>
      </div>
    )
  }

  const handleContext = (name, context) => {
    return (
      <div className="col-4 align-self-center">
        <div className="h2">
          {name}
        </div>
        <p className="sub-text">{context}</p>
      </div>
    )
  }

  return (
    <>
      <div className="pt-6 pb-6" style={{ background: "#130f0a", opacity: ".98" }}>

        <h1 style={titleStyle}>Meet Your Host</h1>

        <div className="pt-5">
          <div className="row justify-content-center">
            {handleImage("shane")}
            {handleContext('Shane Moody', `Born and raised in Denver, Colorado. Played college football at the University of Montana.
      Now a personal trainer at Elite Speed Performance in Denver.`)}
          </div>

          <div className="pt-5">
            <div className="row justify-content-center">
              {handleContext('Christian Backes', `Born and raised in Denver, Colorado. Played college football at the University of Montana.
      Now a personal trainer at Elite Speed Performance in Denver.`)}
              {handleImage("Christian")}
            </div>
          </div>

        </div>

      </div>
    </>
  )
}

export default Team;
