import React from 'react';
import ChristianDrinking from '../images/team/ChristianDrinking.jpg';
import HeaderSection from './components/HeaderSection';
import Team from './components/Team';
import Beer from './components/beer';
import Platforms from './components/Platforms';

const Home = () => {
  const rowStyle = {
    width: "100%",
    maxWidth: "400px",
    maxHeight: "400px",
    position: "absolute",
    transform: "translate(-50%, -50%)",
    left: '50%',
    top: '50%',
    boxShadow: "0 0 1rem #fff",
  }

  return (
    <div className="container-fluid" style={{maxWidth: "1400px", height: "100vh"}}>
      <img src={ChristianDrinking} class="rounded mx-auto d-block" alt="Shane" height="400px" width="400px" style={rowStyle}/>
      <div className="jumbotron">
        <h1 className="display-4">Hello, world!</h1>
        <p className="lead">This is a simple hero unit, a simple jumbotron-style component for calling extra attention to featured content or information.</p>
        <hr className="my-4" />
        <p>It uses utility classes for typography and spacing to space content out within the larger container.</p>
        <a className="btn btn-primary btn-lg" href="#" role="button">Learn more</a>
      </div>
        </div>
  )
}

export default Home;
