import React from 'react';

class UploadBB extends React.Component {
    constructor(props){
        super(props);

        this.state = {
            bbImage: null,
            type: '',
            name: '',
            description: '',
            rating: '',
            comment: ''
        }
    }

    handleInputChanges = (event) => {
        event.target.name === 'bbImage' ? this.setState({ [event.target.name]: event.target.files[0] }) : this.setState({ [event.target.name]: event.target.value })
    }

    handleFormSubmit = (e) => {
        e.preventDefault();

        const { profileImage } = this.state;
        const validImageTypes = ['image/gif', "image/jpeg", 'image/png'];

        if (profileImage === null) {
            return this.setState({ error: [true, 'Please Upload Image (jpgm jpeg, png, or gif)'] })
        }

        if (!validImageTypes.includes(profileImage.type)) {
            return this.setState({ error: [true, 'Images Only. Must Be jpg, jpeg, png, or gif'] });
        }

        // return;
        const data = new FormData();
        data.append('profileImage', profileImage);
        data.append('userId', this.props.userId);
        this.props.change_profile_image(data)
            .then((res) => {
                console.log(res);
            })
            .catch((err) => {
                console.log(err);
            });
    }

    render(){

    return (
        <div>
            <h1>Upload Book and Beer</h1>
            <form onSubmit={this.handleFormSubmit}>
                <select name="type" onChange={this.handleInputChanges} required>
                    <option selected>Pick one</option>
                    <option value="beer">Beer</option>
                    <option value="book">Book</option>
                </select>
                <input type='file' name='bbImage' onChange={this.handleInputChanges} required/>
                <input type='text' name='name' placeholder='name' onChange={this.handleInputChanges} required/>
                <input type='text' name='description' placeholder='From Where?' onChange={this.handleInputChanges} required/>
                <select name="rating" onChange={this.handleInputChanges} required>
                    <option value='1'>1</option>
                    <option value='2'>2</option>
                    <option value='3'>3</option>
                    <option value='4'>4</option>
                    <option value='5'>5</option>
                </select>
                <input type='text' name='comment' placeholder="Any Comments?" onChange={this.handleInputChanges} required/>
            </form>
        </div>
    )
    }
}

export default UploadBB;
